const createXYGridVertices = (gridSize = 10, step = 1) => {
  const vertices = []
  const lines = []

  // Generating vertical lines
  for (let x = -gridSize; x <= gridSize; x += step) {
    vertices.push(x, -gridSize, 0)
    vertices.push(x, gridSize, 0)

    lines.push({start: [x, -gridSize, 0], end: [x, gridSize, 0]})
  }

  for (let y = -gridSize; y <= gridSize; y += step) {
    vertices.push(-gridSize, y, 0)
    vertices.push(gridSize, y, 0)

    lines.push({ start: [-gridSize, y, 0], end: [gridSize, y, 0] })
  }

  return vertices
}


export default createXYGridVertices
