import React, { useRef, useState } from 'react';
import { useWebGL } from './useWebGL';  // Make sure this path is correct
import XYGrid from './Geometries/XY-Grid';
import Cube from './Geometries/Cube';
import Sphere from './Geometries/Sphere';

const CANVAS_WIDTH = 512;
const CANVAS_HEIGHT = 512;

const Sandbox = () => {
    const canvasRef = useRef(null);
    const [settings, setSettings] = useState({
        useWireframe: false,
        showCube: false,
        showXYGrid: true,
        showSphere: false,
        scale: 1,
        rotation: 0,
        translation: 0
    });

    const objectsToDraw = [
        settings.showXYGrid && new XYGrid(),
        settings.showCube && new Cube(1),
        settings.showSphere && new Sphere()
    ].filter(Boolean);  // Filter out falsy values (null, false, undefined)

    useWebGL(canvasRef, settings, objectsToDraw);

    const toggleSetting = (key) => setSettings({...settings, [key]: !settings[key]});

    return (
        <article>
            <p>The sandbox scene is where you can demonstrate features/capabilities of your library.</p>
            <div>
                <button onClick={() => toggleSetting('useWireframe')}>
                    {settings.useWireframe ? 'Show Filled' : 'Show Wireframe'}
                </button>
                <button onClick={() => toggleSetting('showCube')}>
                    {settings.showCube ? 'Remove Cube' : 'Add Cube'}
                </button>
                <button onClick={() => toggleSetting('showXYGrid')}>
                    {settings.showXYGrid ? 'Remove XY Grid' : 'Add XY Grid'}
                </button>
                <button onClick={() => toggleSetting('showSphere')}>
                    {settings.showSphere ? 'Remove Sphere' : 'Add Sphere'}
                </button>
            </div>
            <div>
                <label>Scale:</label>
                <input type="range" min="-2" max="2" step="0.1" value={settings.scale}
                    onChange={e => setSettings({...settings, scale: parseFloat(e.target.value)})} />
                <label>Rotation:</label>
                <input type="range" min="0" max="360" step="1" value={settings.rotation}
                    onChange={e => setSettings({...settings, rotation: parseFloat(e.target.value)})} />
                <label>Translation:</label>
                <input type="range" min="-5" max="5" step="0.1" value={settings.translation}
                    onChange={e => setSettings({...settings, translation: parseFloat(e.target.value)})} />
            </div>
            <canvas ref={canvasRef} width={CANVAS_WIDTH} height={CANVAS_HEIGHT} style={{marginTop: '10px'}} />
        </article>
    );
};

export default Sandbox;
