import Matrix from './Matrix/Matrix'

// provides similar user interface as threejs to not make user manipulate matrix directly for each transformation!
class Transformable {
  constructor(geometry = null) {
    this.position = { x: 0, y: 0, z: 0 }
    this.rotation = { x: 0, y: 0, z: 0 }
    this.scale = { x: 1, y: 1, z: 1 }
    this.matrix = new Matrix()
  }

  updateMatrix() {
    this.matrix.reset()
    this.matrix = Matrix.translation(this.position.x, this.position.y, this.position.z)
      .multiply(Matrix.rotate(this.rotation.x, 1, 0, 0))
      .multiply(Matrix.rotate(this.rotation.y, 0, 1, 0))
      .multiply(Matrix.rotate(this.rotation.z, 0, 0, 1))
      .multiply(Matrix.scale(this.scale.x, this.scale.y, this.scale.z))
  }


  setPosition(x, y, z) {
    this.position = { x, y, z }
    this.updateMatrix()
  }

  setRotation(x, y, z) {
    this.rotation = { x, y, z }
    this.updateMatrix()
  }

  setScale(x, y, z) {
    this.scale = { x, y, z }
    this.updateMatrix()
  }


}

class Group extends Transformable {
  constructor() {
    super()
    this.children = []
  }

  add(child) {
    if (this.children.includes(child)) {
      console.warn('Child already added')
      return
    }
    this.children.push(child)
  }

  remove(child) {
    const index = this.children.indexOf(child)
    if (index !== -1) {
      this.children.splice(index, 1)
    }
  }

  forEachChild(callback) {
    this.children.forEach(callback)
  }

  traverse(callback) {
    callback(this)
    this.children.forEach(child => {
      if (child instanceof Group) {
        child.traverse(callback)
      } else {
        callback(child)
      }
    })
  }
}

export { Transformable, Group }
