/**
 * Build out this component to display a “sandbox” scene—see the description below.
 */
import { useEffect, useRef } from 'react'
import { getGL, initVertexBuffer, compileShader, linkShaderProgram, initSimpleShaderProgram } from './glsl-utils'
import createXYGridVertices from './Geometries/XY-Grid'


const CANVAS_WIDTH = 512
const CANVAS_HEIGHT = 512

const FRAMES_PER_SECOND = 30
const MILLISECONDS_PER_FRAME = 1000 / FRAMES_PER_SECOND

const Sandbox = props => {
  const canvasRef = useRef()
  const timestampRef = useRef()

  useEffect(() => {
    const canvas = canvasRef.current
    if (!canvas) {
      return
    }

    const gl = getGL(canvas)

    if (!gl) {
      console.log('WebGL 2.0 not supported. Falling back on WebGL 1.0.')
      gl = canvas.getContext('webgl')
    }

    if (!gl) {
      alert('Your browser does not support WebGL')
    }

    gl.enable(gl.DEPTH_TEST)
    gl.clearColor(0.0, 0.0, 0.0, 0.0)
    gl.viewport(0, 0, canvas.width, canvas.height)



    // Shader initialization
    // TODO: Initialize your shaders here (vertex shader and fragment shader)
    // This involves compiling shaders and linking them to a shader program

    // Object and scene initialization
    // TODO: Define your 3D objects here
    // This might involve creating vertex buffers, index buffers, and setting up vertex attributes

    const objectsToDraw = [
      {
        color: { r: 0.7, g: 0.7, b: 0.7 }, // Assuming a grey color for the grid lines
        vertices: createXYGridVertices(), // Directly using the vertices from the grid generation function
        mode: gl.LINES
      }
    ]

    // Load buffers with vert

    const drawScene = () => {
      // Clear the display.
      gl.clear(gl.COLOR_BUFFER_BIT | gl.DEPTH_BUFFER_BIT)

      // TODO: Render your objects here. This could involve binding buffers, setting shader uniforms, and making draw calls.

      // Example: gl.drawArrays or gl.drawElements calls for rendering.

      // Display obj (forEach)

      gl.bindBuffer(gl.ARRAY_BUFFER, null)

      // All done.
      // might not be needed
      // gl.flush()
    }

    let previousTimestamp
    const nextFrame = timestamp => {
      // Initialize the timestamp.
      if (!previousTimestamp) {
        previousTimestamp = timestamp
        window.requestAnimationFrame(nextFrame)
        return
      }

      // Check if it’s time to advance.
      const progress = timestamp - previousTimestamp
      if (progress < MILLISECONDS_PER_FRAME) {
        // Do nothing if it’s too soon.
        window.requestAnimationFrame(nextFrame)
        return
      }

      // Update objects' positions, animations, etc., here based on elapsed time

      drawScene()

      if (timestampRef.current) {
        timestampRef.current.innerText = `Timestamp: ${timestamp.toFixed(2)}`
      }

      // Request the next frame.
      previousTimestamp = timestamp
      window.requestAnimationFrame(nextFrame)
    }

    drawScene()

    window.requestAnimationFrame(nextFrame)
  }, [])

  return (
    <article>
      <p>
        The sandbox scene is where you can demonstrate features/capabilities of your library solely for the purpose of
        demonstrating them. It doesn’t have to fit any particular pitch or application.
      </p>

      <div style={{ position: 'relative' }}>
        <canvas width={CANVAS_WIDTH} height={CANVAS_HEIGHT} ref={canvasRef} />
        <div ref={timestampRef} style={{ position: 'absolute', top: '7px', left: '10px', color: 'black' }}>
          Timestamp: 0.00
        </div>
      </div>
    </article>
  )
}

export default Sandbox
